<style>

    .logo{
        display: flex;
        flex-direction: row;
        justify-content: flex-start;
        align-items: center;

        color: #e94368;
        text-decoration: none;
        transition: all .4s;
    }

    .logo svg{
        margin-right: 10px;
        height: 100%;
        width: 40px;
    }

    .logo__text{
        transform: translateX(-50px);
        opacity: 0;
        transition: all .4s;

    }

    .logo div p{
        font-family: 'Open Sans', sans-serif;
        margin:0;
        font-size: 16px;
    }

    .logo div p:last-child{
        font-weight: bolder;
    }

    .logo:hover .logo__text{
        transform: translateX(0);
        opacity: 1;

    }
    @media only screen and (max-width: 768px){
        .logo__text{
            display: none;
        }

        .logo svg{
            margin-right: 0;
        }
    }

</style>

